  var h = 0;

  for (var i = 0; i < str.length; i++) {
    h = ToInt32(ToInt32(31 * h) + str.charCodeAt(i));
  }

  return ToInt32(h ^ seed);
}

function bucket(str
/*: string */
, seed
/*: number */
)
/*: number */
{
  return Math.abs(hash(str, seed) % 100) + 1;
}

export default {
  hash: hash,
  bucket: bucket
};

